const styles = {
    container: {
        padding: '16px',
        backgroundColor: 'white'
    },

    inputDisabled: {
        background: '#efefef'
    },

    input : {
        width: '100%',
        padding: '15px',
        margin: '5px 0 22px 0',
        display: 'inline-block',
        border: '1px solid black',
        background: 'white'
    },

    hr : {
        border: "1px solid #f1f1f1",
        marginBottom: "25px"
    },

    boldText : {
        fontWeight: 'bold'
    },

    success : {
        border:'1px solid chartreuse !important'
    },

    error : {
        border:'1px solid red !important'
    }

}

export default styles;
